import React from 'react';
import Answer_Card from '../Answer_Card/Answer_Card';
import { Random } from "random-js";

const Answer_Container = () => {
    const random = new Random();
    let answers = [];
    let answer = 4;

    const shuffle = (array) => {
        var currentIndex = array.length
          , temporaryValue
          , randomIndex
          ;

        // While there remain elements to shuffle...
        while (0 !== currentIndex) {

          // Pick a remaining element...
          randomIndex = random.integer(0,currentIndex);
          currentIndex -= 1;

          // And swap it with the current element.
          temporaryValue = array[currentIndex];
          array[currentIndex] = array[randomIndex];
          array[randomIndex] = temporaryValue;
        }

        return array;
      };

    answers.push(answer);

    for (let i = 0; i < 3; i++) {
        answers.push(random.integer(0,20));
    }

    answers = shuffle(answers);


    return (
        <div>
            <Answer_Card answer = {answers[0]}/>
            <Answer_Card answer = {answers[1]}/>
            <Answer_Card answer = {answers[2]}/>
            <Answer_Card answer = {answers[3]}/>
        </div>
    );
}

export default Answer_Container;
